import XCTest
@testable import DNSKit

final class DNSSECClientTests: XCTestCase {
    func validateWithMessage(_ message: Message) throws {
        let rrsigAnswer = message.answers.first(where: { answer in answer.recordType == .RRSIG })!
        let rrsig = rrsigAnswer.data as! RRSIGRecordData

        var dnskeyAnswer: Answer!
        var answers: [Answer] = []
        for answer in message.answers {
            if answer.recordType != .DNSKEY {
                continue
            }

            answers.append(answer)

            let dnskey = answer.data as! DNSKEYRecordData
            if dnskey.keyTag != rrsig.keyTag {
                continue
            }
            dnskeyAnswer = answer
        }

        try DNSSECClient.validateAnswers(answers, signatureAnswer: rrsigAnswer, dnskeyAnswer: dnskeyAnswer)
    }

